/* Adjust the foreground or background color until contrast ≥4.5:1 */
/* Use CSS custom properties to keep the fix systematic: */
.element {
  color: var(--color-text-primary); /* verify ≥4.5:1 against background */
  background-color: var(--color-surface); /* e.g. #fff or #1e293b */
}
/* Tip: Check contrast at https://webaim.org/resources/contrastchecker/ */

/* Minimum WCAG 2.2 AA target size */
.btn, a, [role="button"] {
  min-width: 24px;
  min-height: 24px;
}

/* Recommended for touch: */
.btn {
  min-width: 44px;
  min-height: 44px;
  padding: 0.5rem 1rem;
}

:focus-visible {
  outline: 3px solid #005FCC;
  outline-offset: 2px;
  /* Ensure contrast ratio ≥ 3:1 vs adjacent background */
}

/* Remove browser default only when providing a custom style */
:focus:not(:focus-visible) {
  outline: none;
}
