code_example.before:
/* BEFORE — browser default outline suppressed */
*:focus {
  outline: none;
}

code_example.after:
/* AFTER — accessible focus indicator */
*:focus-visible {
  outline: 3px solid #005FCC;
  outline-offset: 2px;
  border-radius: 2px;
}
/* Only suppress for mouse/pointer interactions */
*:focus:not(:focus-visible) {
  outline: none;
}

code_example.before:
/* BEFORE — icon button renders below 24×24 CSS px */
.icon-btn {
  width: 16px;
  height: 16px;
  padding: 0;
}

code_example.after:
/* AFTER — minimum 24×24 CSS px via padding */
.icon-btn {
  min-width: 24px;
  min-height: 24px;
  padding: 4px;
  box-sizing: content-box;
}

code_example.before:
// BEFORE — widget traps all Tab keypresses, focus cannot exit
widget.addEventListener('keydown', (e) => {
  if (e.key === 'Tab') {
    e.preventDefault(); // blocks exit
    cycleFocusInternally(e.shiftKey);
  }
});

code_example.after:
// AFTER — focus trap only inside open modal dialogs
widget.addEventListener('keydown', (e) => {
  const isModal = widget.getAttribute('role') === 'dialog';
  if (e.key === 'Tab' && isModal && isOpen) {
    trapFocusWithin(widget, e);
  }
  // Non-modal: let Tab exit naturally
});

code_example.before:
/* BEFORE — fixed-width layout, causes horizontal scroll at 320px */
.container {
  width: 960px;
}
.sidebar {
  width: 300px;
  float: left;
}

code_example.after:
/* AFTER — responsive layout, single column at 320px */
.container {
  width: 100%;
  max-width: 960px;
  padding: 0 1rem;
  box-sizing: border-box;
}
.sidebar { width: 100%; }
@media (min-width: 640px) {
  .sidebar {
    width: 30%;
    float: left;
  }
}

code_example.before:
/* BEFORE — input border fails 3:1 against white background */
input[type="text"] {
  border: 1px solid #ccc; /* ~1.6:1 contrast */
}
.icon { fill: #bbb; } /* ~1.4:1 contrast */

code_example.after:
/* AFTER — border and icon meet 3:1 minimum */
input[type="text"] {
  border: 1px solid #767676; /* ~4.5:1 against white */
}
.icon { fill: #595959; } /* ~7:1 against white */

code_example.before:
/* BEFORE — fixed height clips text with custom user spacing */
.card-header {
  height: 48px;
  overflow: hidden;
}
.btn {
  height: 40px;
  line-height: 40px;
}

code_example.after:
/* AFTER — min-height allows text to grow with user spacing overrides */
.card-header {
  min-height: 48px;
  overflow: visible;
}
.btn {
  min-height: 40px;
  padding: 0.5em 1em;
  line-height: 1.5;
}

