code_example.before:
@keyframes flash {
  0%, 50% { background: white; }
  51%, 100% { background: black; }
}
.alert { animation: flash 0.2s infinite; }

code_example.after:
@media (prefers-reduced-motion: no-preference) {
  @keyframes fade {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }
  .alert { animation: fade 0.8s ease-in-out infinite; }
}

code_example.before:
// BEFORE — action only triggered by device motion
window.addEventListener('devicemotion', (e) => {
  if (e.acceleration.x > 15) undoLastAction()
})

code_example.after:
// AFTER — button alternative + motion can be disabled
let motionEnabled = true
document.getElementById('undo-btn').addEventListener('click', undoLastAction)
window.addEventListener('devicemotion', (e) => {
  if (motionEnabled && e.acceleration.x > 15) undoLastAction()
})
